import UIKit

protocol WeatherPresentationLogic
{
  func presentWeather(response: Weather.Get.Response)
}

class WeatherPresenter: WeatherPresentationLogic
{
  weak var viewController: WeatherDisplayLogic?
  
  // MARK: Do something
  
  func presentWeather(response: Weather.Get.Response)
  {

   let wResponse  = response.item
        let spped = "\(wResponse.windSpeed)"
        let temp = "\(wResponse.temperature)ยบ C"
        let model = Weather.Get.ViewModel.Weather(city: wResponse.city, temperature: temp, cloudiness: wResponse.cloudiness, description: wResponse.description, windSpeed: spped)
        let viewModel = Weather.Get.ViewModel(weather: model)
    viewController?.displayWeather(viewModel: viewModel)
  }
}
